import React, { useState, useEffect, useRef } from "react";
import Webcam from "react-webcam";

const useNotification = (title, options) => {
  if (!("Notification" in window)) {
    return;
  }

  const fireNotif = () => {
    /* 권한 요청 부분 */
    if (Notification.permission !== "granted") {
      Notification.requestPermission().then((permission) => {
        if (permission === "granted") {
          /* 권한을 요청받고 nofi를 생성해주는 부분 */
          new Notification(title, options);
        } else {
          return;
        }
      });
    } else {
      /* 권한이 있을때 바로 noti 생성해주는 부분 */
      new Notification(title, options);
    }
  };
  return fireNotif;
};

function Pose(pros) {
  const [posture, setPosture] = useState("0");

  useEffect(() => {
    let a;
    if (posture == 1) a = new Notification("거북목 자세입니다!");
    else if (posture == 2) a = new Notification("턱을 괸 자세입니다!");
  }, [posture]);

  const videoConstraints = {
    width: 1280,
    height: 720,
    facingMode: "user",
  };

  const WebcamCapture = () => {
    const webcamRef = useRef();

    const capture = () => {
      setTimeout(function () {
        var imageSrc;

        console.log(webcamRef.current);

        if (webcamRef.current == null)
          imageSrc = "C:\\Users\\82109\\Downloads\\pose_capture.jpg";
        else imageSrc = webcamRef.current.getScreenshot();

        console.log(imageSrc);

        fetch("http://localhost:4001/pose", {
          method: "post",
          headers: {
            "content-type": "application/json",
          },
        })
          .then((res) => res.json())
          .then((json) => {
            setPosture(json.text);
            console.log("qwerqwer");
            console.log(json.text);
          });

        var a = document.createElement("a");
        a.style = "display: none";
        a.href = imageSrc;
        a.download = "pose_capture.jpg";

        document.body.appendChild(a);

        a.click();

        setTimeout(function () {
          // 다운로드가 안되는 경우 방지
          document.body.removeChild(a);
        }, 100);
      }, 10);
    };

    useEffect(() => {
      setInterval(() => {
        capture();
      }, 5000);
    }, []);

    return (
      <>
        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          videoConstraints={videoConstraints}
          mirrored={true}
          height={0.6 * `${window.innerHeight}`}
          width={0.9 * `${window.innerWidth}`}
        />
      </>
    );
  };

  return (
    <div className="pose">
      <WebcamCapture />
      <div>
        {posture == 0 && "바른 자세입니다."}
        <div style={{ color: "red" }}>
          {posture == 1 && "거북목 자세입니다."}
          {posture == 2 && "턱을 괸 자세입니다."}
        </div>
      </div>
    </div>
  );
}

export default Pose;
